var nodemailer = require('nodemailer');

class sendMail {
    transporter(service, user, pass) {
        return nodemailer.createTransport({
            service: service,
            auth: {
                user: user,
                pass: pass
            }
        });
    }

    mailOptions(from, to, subject, text) {
        const mailOptions = {
            from: from,
            to: to,
            subject: subject,
            text: text
        };
        return mailOptions
    }
}

module.exports = new sendMail();